import React from "react";
import Content from "../../../Layout/Content/Content";
import Formikform from "../../../Helpers/Form";
// import * as valid_err from "../../../Utils/Common_Messages";
import { useFormik } from "formik";

const Add_User = () => {
  const formik = useFormik({
    initialValues: {
      mobileNumber: "",
      password: "",
      name: "",
      viewUser: false,
      createUser: false,
      viewGame: false,
      gameDelete: false,
      gameEdit: false,
      gameCreated: false,
    },

    validate: (values) => {
      const errors = {};

      if (!values.password) {
        errors.password = valid_err.PASSWORD_ERROR;
      } else if (!isValidPassword(values.password)) {
        errors.password = valid_err.PASSWORD__LENGTH_ERROR;
      }
      if (!values.mobileNumber) {
        errors.mobileNumber = valid_err.CONTACT_ERROR;
      } else if (!isValidContact(values.mobileNumber)) {
        errors.mobileNumber = valid_err.INVALID_CONTACT_ERROR;
      }

      // if (!values.port) {
      //   errors.port = valid_err.PORT_ERROR;
      // }

      // if (!values.key) {
      //   errors.key = valid_err.KEY_ERROR;
      // }

      // if (!values.db_url) {
      //   errors.db_url = valid_err.DBURL_ERROR;
      // }

      // if (!values.db_name) {
      //   errors.db_name = valid_err.DBNAME_ERROR;
      // }

      // if (!values.backend_rul) {
      //   errors.backend_rul = valid_err.DBNAME_ERROR;
      // }

      return errors;
    },
    onSubmit: async (values) => {
      const req = {
        // panel_name: values.panel_name,
        // domain: values.domain,
        // port: values.port,
        // key: values.key,
        // ip_address: values.ip_address,
        // theme_id: values.theme_id,
        // db_url: values.db_url,
        // db_name: values.db_name,
        // broker_id: state1,
        // Create_Strategy:
        //   values.Create_Strategy && values.Create_Strategy ? 1 : 0,
        // Option_chain: values.Option_chain && values.Option_chain ? 1 : 0,
        // Strategy_plan: values.Strategy_plan && values.Strategy_plan ? 1 : 0,
        // backend_rul: values.backend_rul,
      };

      // await dispatch(Add_Panel_data({ req: req, token: user_token }))
      //   .unwrap()
      //   .then((response) => {
      //     if (response.status === 409) {
      //       toast.error(response.data.msg);
      //     } else if (response.status) {
      //       toast.success(response.msg);

      //       setTimeout(() => {
      //         navigate("/super/alladmins");
      //       }, 1000);
      //     } else if (!response.status) {
      //       toast.error(response.msg);
      //     }
      //   });
    },
  });

  const fields = [
    {
      name: "mobileNumber",
      label: "Mobile Number",
      type: "text",
      label_size: 12,
      col_size: 6,
    },
    {
      name: "password",
      label: "Password",
      type: "password",
      label_size: 12,
      col_size: 6,
    },

    {
      name: "name",
      label: "Name",
      type: "text",
      label_size: 12,
      col_size: 6,
    },
  ];

  const permisstionArray = [
    {
      id: 0,
      name: "Game Create",
      value: "gameCreated",
    },
    {
      id: 1,
      name: "Edit Game ",
      value: "gameEdit",
    },
    {
      id: 2,
      name: "Delete Game",
      value: "gameDelete",
    },
    {
      id: 3,
      name: "View Game",
      value: "viewGame",
    },
    {
      id: 4,
      name: " Create User",
      value: "createUser",
    },
    {
      id: 5,
      name: "View Users",
      value: "viewUser",
    },
  ];

  return (
    <>
      <Content title="Add User" col_size={12}>
        <Formikform
          fieldtype={fields.filter((field) => !field.showWhen)}
          formik={formik}
          btn_name="Add User"
          before_submit={
            <>
              <h5 className="fw-bold"> Permissions </h5>
              {permisstionArray.map((broker) => (
                <div className={`col-lg-4 mt-2`}>
                  <div className="row ">
                    <div className="col-lg-12 ">
                      <div class="form-check custom-checkbox mb-3">
                        <input
                          type="checkbox"
                          className="form-check-input"
                          name={broker.name}
                          value={broker.id}
                          onChange={(e) => handleSBrokerChange(e, name)}
                        />
                        <label className="form-check-label" for={broker.name}>
                          {broker.name}
                        </label>
                      </div>
                    </div>
                  </div>
                </div>
              ))}

              {/* {formik.errors.title && (
                <div style={{ color: "red" }}>{formik.errors.title}</div>
              )} */}
            </>
          }
        />
      </Content>
    </>
  );
};

export default Add_User;
