import React from "react";
import Content from "../../Layout/Content/Content1";
import Formikform from "../../Helpers/Form";
import { useFormik } from "formik";
import { Link } from "react-router-dom";
import { Sign_Up } from "../../Redux/Slice/Auth/auth.slice";
import { Name_regex, Mobile_regex } from "../../Utils/Valid_Rejex";
import ToastButton from "../../Helpers/Toast";

import * as valid_err from "../../Utils/Common_Msg";

const Users = () => {
  const isValidContact = (mobile) => {
    return Mobile_regex(mobile);
  };
  const isValidName = (mobile) => {
    return Name_regex(mobile);
  };

  const formik = useFormik({
    initialValues: {
      name: "",
      mobile: "",
      password: "",
    },

    validate: (values) => {
      const errors = {};

      if (!isValidName(values.name) && formik.touched.name) {
        errors.name = valid_err.INVALID_ERROR;
      }
      if (!values.mobile && formik.touched.mobile) {
        errors.mobile = valid_err.CONTACT_ERROR;
      } else if (!isValidContact(values.mobile) && formik.touched.mobile) {
        errors.mobile = valid_err.INVALID_CONTACT_ERROR;
      }

      if (!values.password) {
        errors.password = valid_err.PORT_ERROR;
      }

      return errors;
    },
    onSubmit: async (values) => {
      const req = {
        name: values.name,
        mobile: values.mobile,
        password: values.password,
      };

      await dispatch(Sign_Up({ req: req }))
        .unwrap()
        .then((response) => {
          if (response.status === 409) {
            toast.error(response.data.msg);
          } else if (response.status) {
            toast.success(response.msg);
            setTimeout(() => {
              navigate("/");
            }, 1000);
          } else if (!response.status) {
            toast.error(response.msg);
          }
        });
    },
  });

  const fields = [
    {
      name: "name",
      label: "Name",
      type: "text",
      label_size: 12,
      col_size: 12,
    },
    {
      name: "mobile",
      label: "Mobile",
      type: "text",
      label_size: 12,
      col_size: 12,
    },
    {
      name: "password",
      label: "Password",
      type: "password",
      label_size: 12,
      col_size: 12,
    },
  ];

  return (
    <>
      <Content
        title="Sign Up User"
        col_size={7}
        responsive_col={"col-md-8 col-lg-6"}
      >
        <Formikform
          fieldtype={fields.filter((field) => !field.showWhen)}
          formik={formik}
          btn_name="Create My Account "
          btn_Class="btn btn-primary btn-block"
          additional_field={
            <>
              {formik.errors.title && (
                <div style={{ color: "red" }}>{formik.errors.title}</div>
              )}
            </>
          }
        />

        <p className="text-center my-3">
          Already have an account?
          <Link to="/">&nbsp; Sign in</Link>
        </p>
      </Content>

      <ToastButton />
    </>
  );
};

export default Users;
