import { createSlice, createReducer, createAsyncThunk } from "@reduxjs/toolkit";
// // import { DispatchLogin } from "../../../Layout/Auth/Login";
import { LOGIN } from "../../../Service/auth.service";

export const Login = createAsyncThunk("auth/login", async (data) => {
  const { user_id, token } = data;

  try {
    const res = await LOGIN({ _id: user_id }, token);
    return await res;
  } catch (err) {
    return err;
  }
});

const AuthSlice = createSlice({
  name: "AuthSlice",
  initialState: {
    isLoading: false,
    isError: false,
    helps: [],
    status: false,
  },

  recuders: {},
  extraReducers: 
  // {
  //   [Login.pending]: (state, { payload }) => {
  //     // state.isLoading = false;
  //     // return { ...state, get_dashboard: [], isLoading: true };
  //   },
  //   [Login.fulfilled]: (state, { payload }) => {
  //     // state.isLoading = false;
  //     return { ...state, helps: payload, isLoading: false };
  //   },
  //   [Login.rejected]: (state, action) => {
  //     // return { ...state, get_dashboard: action, isLoading: false };
  //   },
  // },

  (builder) => {

    builder
      .addCase(Login.pending, (state, action) => {
        return { ...state, auth_Info: [], isLoading: true };
      })
      .addCase(Login.fulfilled, (state, action) => {
        return { ...state, auth_Info: action.payload, isLoading: false };
      })
      .addCase(Login.rejected, (state, action) => {
        return { ...state, auth_Info: [], isLoading: false };
      });
  },
});

export default AuthSlice;

