import React, { useState, useEffect } from "react";
import Content from "../../Layout/Content/Content1";
import Formikform from "../../Helpers/Form";
import { useFormik } from "formik";
import { useDispatch, useSelector } from "react-redux";
import toast from "react-hot-toast";
import { Link, useNavigate } from "react-router-dom";
import {
  Send_OTP,
  Sign_Up,
  Verify_OTP,
} from "../../Redux/Slice/Auth/auth.slice";
import { Name_regex, Mobile_regex } from "../../Utils/Valid_Rejex";
import ToastButton from "../../Helpers/Toast";
import OTPInput, { ResendOTP } from "otp-input-react";
import * as valid_err from "../../Utils/Common_Msg";

const Users = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const [OTP, setOTP] = useState("");
  const [ShowOTP, setShowOTP] = useState(false);
  const [Disabled, setDisabled] = useState(false);

  const isValidContact = (mobile) => {
    return Mobile_regex(mobile);
  };

  const isValidName = (name) => {
    return Name_regex(name);
  };

  const formik = useFormik({
    initialValues: {
      name: "",
      mobileNumber: "",
      password: "",
    },

    validate: (values) => {
      const errors = {};

      if (!isValidName(values.name)) {
        errors.name = valid_err.INVALID_ERROR;
      }
      if (!values.mobileNumber) {
        errors.mobileNumber = valid_err.CONTACT_ERROR;
      } else if (!isValidContact(values.mobileNumber)) {
        errors.mobileNumber = valid_err.INVALID_CONTACT_ERROR;
      }

      if (!values.password) {
        errors.password = valid_err.PASSWORD_ERROR;
      }

      return errors;
    },
    onSubmit: async (values) => {
      const req = {
        name: values.name,
        mobileNumber: values.mobileNumber,
        password: values.password,
      };

      await dispatch(Sign_Up(req))
        .unwrap()
        .then((response) => {
          if (response.status === 400) {
            toast.error(response.data.msg);
          } else if (response.status) {
            toast.success(response.msg);
            setTimeout(() => {
              navigate("/");
            }, 1000);
          } else if (!response.status) {
            toast.error(response.msg);
          }
        });
    },
  });

  const fields = [
    {
      name: "name",
      label: "Name",
      type: "text",
      label_size: 12,
      col_size: 12,
      showButton: false,
    },

    {
      name: "password",
      label: "Password",
      type: "password",
      label_size: 12,
      col_size: 12,
      showButton: false,
    },
    {
      name: "mobileNumber",
      label: "Mobile",
      type: "text",
      label_size: 12,
      col_size: 12,
      showButton: true,
    },
  ];

  const VerifyMobileN = async () => {
    setShowOTP(true);

    const res = await dispatch(
      Send_OTP({ mobileNumber: formik.values.mobileNumber })
    ).unwrap();

    if (res.status) {
      setDisabled(true);
    } else {
      toast.error(res.msg);
      setDisabled(false);
    }
  };

  const Verify_OTP_Function = (e) => {
    e.preventDefault();
    alert("tt");
  };

  // const renderButton = (buttonProps) => {
  //   return <button {...buttonProps}>Resend</button>;
  // };
  // const renderTime = (remainingTime) => {
  //   return <span>{remainingTime} seconds remaining</span>;
  // };

  // Verify_OTP

  // useEffect(() => {
  //   for (let index = 0; index < 59; index--) {
  //     const element = index;
  //     console.log("element", element);
  //   }
  // }, []);

  const renderButton = (buttonProps) => {
    return (
      <button className="btn btn-primary text-light" {...buttonProps}>
        {buttonProps.remainingTime !== 0
          ? `Please wait for ${buttonProps.remainingTime} sec`
          : "Resend"}
      </button>
    );
  };
  const renderTime = () => React.Fragment;

  return (
    <>
      <Content
        title="Sign Up User"
        col_size={7}
        responsive_col={"col-md-8 col-lg-6"}
      >
        <Formikform
          fieldtype={fields.filter((field) => !field.showWhen)}
          formik={formik}
          VerifyMobileN={VerifyMobileN}
          btn_name="Create My Account "
          button_Size="col-12"
          btn_Class="btn btn-primary btn-block"
          Disable_Button={Disabled}
          additional_field={
            <>
              {!ShowOTP ? (
                <>
                  <div className="col-12 d-flex align-items-center justify-content-between">
                    <div className="">
                      <label className={`col-lg-12`} htmlFor={"VerifyOTP"}>
                        Enter Otp
                        <span className="text-danger">*</span>
                      </label>
                      <OTPInput
                        value={OTP}
                        inputClassName="form-control col-12 px-2"
                        name="VerifyOTP"
                        onChange={setOTP}
                        autoFocus
                        OTPLength={4}
                        otpType="number"
                        disabled={false}
                        className="col-6 col-sm-12 mb-3 "
                      />
                    </div>
                    <ResendOTP
                      renderButton={renderButton}
                      renderTime={renderTime}
                    />
                    <button className="btn btn-primary">Verify </button>
                  </div>
                </>
              ) : (
                ""
              )}
              {formik.errors.title && (
                <div style={{ color: "red" }}>{formik.errors.title}</div>
              )}
            </>
          }
        />

        <p className="text-center my-3">
          Already have an account?
          <Link to="/">&nbsp; Sign in</Link>
        </p>
      </Content>

      <ToastButton />
    </>
  );
};

export default Users;
