import React, { useState } from "react";
import Content from "../../Layout/Content/Content1";
import Formikform from "../../Helpers/Form";
import { useFormik } from "formik";
import { useDispatch, useSelector } from "react-redux";
import toast from "react-hot-toast";
import { Link, useNavigate } from "react-router-dom";
import { Sign_Up } from "../../Redux/Slice/Auth/auth.slice";
import { Name_regex, Mobile_regex } from "../../Utils/Valid_Rejex";
import ToastButton from "../../Helpers/Toast";
import OTPInput, { ResendOTP } from "otp-input-react";

import * as valid_err from "../../Utils/Common_Msg";

const Users = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const [OTP, setOTP] = useState("");

  const isValidContact = (mobile) => {
    return Mobile_regex(mobile);
  };
  const isValidName = (name) => {
    return Name_regex(name);
  };

  const formik = useFormik({
    initialValues: {
      name: "",
      mobileNumber: "",
      password: "",
    },

    validate: (values) => {
      const errors = {};

      if (!isValidName(values.name)) {
        errors.name = valid_err.INVALID_ERROR;
      }
      if (!values.mobileNumber) {
        errors.mobileNumber = valid_err.CONTACT_ERROR;
      } else if (!isValidContact(values.mobileNumber)) {
        errors.mobileNumber = valid_err.INVALID_CONTACT_ERROR;
      }

      if (!values.password) {
        errors.password = valid_err.PASSWORD_ERROR;
      }

      return errors;
    },
    onSubmit: async (values) => {
      const req = {
        name: values.name,
        mobileNumber: values.mobileNumber,
        password: values.password,
      };

      await dispatch(Sign_Up(req))
        .unwrap()
        .then((response) => {
          if (response.status === 400) {
            toast.error(response.data.msg);
          } else if (response.status) {
            toast.success(response.msg);
            setTimeout(() => {
              navigate("/");
            }, 1000);
          } else if (!response.status) {
            toast.error(response.msg);
          }
        });
    },
  });

  const fields = [
    {
      name: "name",
      label: "Name",
      type: "text",
      label_size: 12,
      col_size: 12,
    },

    {
      name: "password",
      label: "Password",
      type: "password",
      label_size: 12,
      col_size: 12,
    },
    {
      name: "mobileNumber",
      label: "Mobile",
      type: "text",
      label_size: 12,
      col_size: 8,
    },
  ];

  return (
    <>
      <Content
        title="Sign Up User"
        col_size={7}
        responsive_col={"col-md-8 col-lg-6"}
      >
        <Formikform
          fieldtype={fields.filter((field) => !field.showWhen)}
          formik={formik}
          btn_name="Create My Account "
          btn_Class="btn btn-primary btn-block"
          additional_field={
            <>
              {formik.errors.title && (
                <div style={{ color: "red" }}>{formik.errors.title}</div>
              )}
            </>
          }
        />

        <p className="text-center my-3">
          Already have an account?
          <Link to="/">&nbsp; Sign in</Link>
        </p>

        <OTPInput
          value={OTP}
          onChange={setOTP}
          autoFocus
          OTPLength={4}
          otpType="number"
          disabled={false}
          secure
        />
        <ResendOTP onResendClick={() => console.log("Resend clicked")} />
      </Content>

      <ToastButton />
    </>
  );
};

export default Users;

